    // --- ESTRUTURAS DE AGENTES ---
    const STRUCTURES = {
        'A': { name: "Refinamento", agents: [{id:'dev',name:'Dev',icon:'üí°',role:'Criar'}, {id:'rev',name:'Revisor',icon:'üîç',role:'Criticar'}, {id:'fix',name:'Fixer',icon:'üõ†Ô∏è',role:'Corrigir'}] },
        'B': { name: "Debate", agents: [{id:'pro',name:'Tese',icon:'üöÄ',role:'Arrojado'}, {id:'con',name:'Ant√≠tese',icon:'üõ°Ô∏è',role:'Seguro'}, {id:'judge',name:'Juiz',icon:'‚öñÔ∏è',role:'Decidir'}] },
        'D': { name: "F√°brica de Arquivos", agents: [{id:'arch',name:'Arquiteto',icon:'üìê',role:'Listar arquivos'}, {id:'dev',name:'Dev',icon:'üë®‚Äçüíª',role:'Codar arquivo'}] },
        'E': { name: "Monolith Builder", agents: [{id:'core',name:'Core',icon:'üèóÔ∏è',role:'Base'}, {id:'ui',name:'UI',icon:'üé®',role:'Estilo'}, {id:'full',name:'Fullstack',icon:'üöÄ',role:'Expandir'}] },
        'F': { name: "Block Builder (Costura)", agents: [{id:'skel',name:'Esqueleto',icon:'üíÄ',role:'HTML Vazio'}, {id:'css',name:'CSS',icon:'üé®',role:'Apenas CSS'}, {id:'js',name:'JS',icon:'üß†',role:'Apenas JS'}] },
        'G': { name: "Auditoria", agents: [{id:'dev',name:'R√©u (Dev)',icon:'üë®‚Äçüíª',role:'Gerar C√≥digo'}, {id:'audit',name:'Auditor',icon:'üìã',role:'Avaliar (0-100)'}, {id:'senior',name:'Senior',icon:'üé©',role:'Refatorar se < 80'}] }
    };

    let state = { isRunning: false, history: [], masterFile: "", loop: 0, agentIdx: 0, structure: 'F' };

    window.onload = () => {
        updateVisualizer();
        updateModelSuggestions();
    };

    function updateModelSuggestions() {
        const providerKey = document.getElementById('providerSelect').value;
        const datalist = document.getElementById('modelList');
        const modelInput = document.getElementById('modelId');

        datalist.innerHTML = "";
        const models = PROVIDERS[providerKey].models;

        models.forEach(m => {
            const opt = document.createElement('option');
            opt.value = m;
            datalist.appendChild(opt);
        });

        modelInput.value = models[0];

        const root = document.documentElement;
        if(providerKey === 'groq') root.style.setProperty('--app-accent', '#f55036');
        else if(providerKey === 'openai') root.style.setProperty('--app-accent', '#10a37f');
        else if(providerKey === 'gemini') root.style.setProperty('--app-accent', '#4285f4');
        else if(providerKey === 'deepseek') root.style.setProperty('--app-accent', '#4d6bfe');
        else root.style.setProperty('--app-accent', '#3b82f6');
    }

    function updateVisualizer() {
        const type = document.getElementById('structureSelect').value;
        const container = document.getElementById('visualizerContainer');
        const agents = STRUCTURES[type].agents;

        let html = `<div class="structure-diagram">`;
        agents.forEach((ag, i) => {
            html += `<div class="agent-node" id="node-${i}"><span class="agent-icon">${ag.icon}</span><strong>${ag.name}</strong><br><span>${ag.role}</span></div>`;
        });
        html += `</div>`;
        container.innerHTML = html;
        state.structure = type;
    }

    async function startOrchestration() {
        if(state.isRunning) return;

        const isMock = document.getElementById('mockMode').checked;
        const apiKey = document.getElementById('apiKey').value;

        if(!isMock && !apiKey) { alert("Falta API Key!"); return; }

        state.isRunning = true;
        state.loop = 1;
        state.agentIdx = 0;
        state.masterFile = "";
        state.history = [];

        document.getElementById('logContainer').innerHTML = '';
        document.getElementById('startBtn').disabled = true;
        document.getElementById('statusText').innerText = "Iniciando Agentes...";

        runStep();
    }

    async function runStep() {
        if(!state.isRunning) return;

        const struct = STRUCTURES[state.structure];
        const agent = struct.agents[state.agentIdx];
        const isMock = document.getElementById('mockMode').checked;
        const userProblem = document.getElementById('userProblem').value;

        // UI Updates
        document.querySelectorAll('.agent-node').forEach(n => n.classList.remove('active'));
        const node = document.getElementById(`node-${state.agentIdx}`);
        if(node) node.classList.add('active');
        document.getElementById('statusText').innerText = `Agente: ${agent.name} (Loop ${state.loop})`;

        // Prompt Logic
        let systemMsg = `Voc√™ √© ${agent.name}. Fun√ß√£o: ${agent.role}. Contexto: ${userProblem}.`;
        let context = state.history;

        // L√≥gica F (Block Builder)
        if (state.structure === 'F') {
            if (agent.id === 'skel') systemMsg += " GERE APENAS O HTML VAZIO com tags <style>/* CSS_PLACEHOLDER */</style> e <script>// JS_PLACEHOLDER<\/script>.";
            else if (agent.id === 'css') {
                systemMsg += " GERE APENAS O C√ìDIGO CSS PURO. N√£o use tags <style>.";
                context = [{role:'user', content: `O HTML √â: ${state.masterFile}. Gere o CSS.`}];
            }
            else if (agent.id === 'js') {
                systemMsg += " GERE APENAS O C√ìDIGO JS PURO. N√£o use tags <script>.";
                context = [{role:'user', content: `O HTML √â: ${state.masterFile}. Gere o JS.`}];
            }
        }
        // L√≥gica G (Auditor)
        else if (state.structure === 'G') {
             if (agent.id === 'audit') {
                 systemMsg = `ATUE COMO AUDITOR S√äNIOR. Avalie o c√≥digo anterior de 0 a 100.
                 Crit√©rios: Modularidade, Seguran√ßa e Performance.
                 Se a nota for < 80, aponte o erro fatal.
                 SA√çDA ESPERADA: Comece com 'NOTA: XX'. Depois explique.`;
             }
        }

        try {
            let response = "";
            if(isMock) {
                await new Promise(r => setTimeout(r, 1000));
                response = "(Simula√ß√£o) C√≥digo gerado...";
                if(state.structure === 'F' && agent.id === 'skel') response = "<html><style>/* CSS_PLACEHOLDER */</style><body><div id='app'></div></body><script>// JS_PLACEHOLDER<\/script></html>";
            } else {
                response = await callUnifiedAPI(systemMsg, context);
            }

            // Post-Process
            if (state.structure === 'F') {
                let clean = response.replace(/```(html|css|js|javascript)?/g, '').replace(/```/g, '').trim();
                if(agent.id === 'skel') state.masterFile = clean;
                if(agent.id === 'css') state.masterFile = state.masterFile.replace('/* CSS_PLACEHOLDER */', clean);
                if(agent.id === 'js') state.masterFile = state.masterFile.replace('// JS_PLACEHOLDER', clean);

                logToScreen(agent, `Bloco gerado. Tamanho Total: ${state.masterFile.length} chars.`);
            } else {
                logToScreen(agent, response);
                state.history.push({role:'assistant', content: response});
                if(response.includes('<html')) state.masterFile = response;
            }

            // Next Step
            state.agentIdx++;
            if(state.agentIdx >= struct.agents.length) {
                state.agentIdx = 0;
                state.loop++;
                if(state.structure === 'F') { stop(true); return; }
            }

            if(state.loop > document.getElementById('maxLoops').value) stop(true);
            else runStep();

        } catch (e) {
            logToScreen({name:'ERRO', icon:'‚ùå'}, e.message);
            stop(false);
        }
    }

    async function callUnifiedAPI(sysPrompt, msgs) {
        const providerKey = document.getElementById('providerSelect').value;
        const apiKey = document.getElementById('apiKey').value;
        const model = document.getElementById('modelId').value;
        const config = PROVIDERS[providerKey];

        let url = typeof config.url === 'function' ? config.url(model, apiKey) : config.url;
        let headers = config.headers(apiKey);
        let body = {};

        if (config.type === 'google_native') {
            let promptText = `${sysPrompt}\n\n`;
            msgs.forEach(m => promptText += `${m.role.toUpperCase()}: ${m.content}\n`);
            promptText += "MODEL:";
            body = { contents: [{ parts: [{ text: promptText }] }] };
        } else {
            let fullMessages = [{ role: 'system', content: sysPrompt }, ...msgs];
            body = { model: model, messages: fullMessages, temperature: 0.7 };
        }

        const req = await fetch(url, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', ...headers },
            body: JSON.stringify(body)
        });

        if(!req.ok) {
            const err = await req.json();
            throw new Error(err.error?.message || JSON.stringify(err));
        }

        const data = await req.json();

        if (config.type === 'google_native') {
            return data.candidates[0].content.parts[0].text;
        } else {
            return data.choices[0].message.content;
        }
    }

    function stop(success) {
        state.isRunning = false;
        document.getElementById('startBtn').disabled = false;
        document.getElementById('statusText').innerText = success ? "Conclu√≠do!" : "Parado com Erro";
        if(success && state.masterFile) {
            logToScreen({name:'SISTEMA', icon:'üíæ'}, `### ARQUIVO FINAL:\n\`\`\`html\n${state.masterFile}\n\`\`\``);
        }
    }

    function logToScreen(agent, text) {
        const div = document.createElement('div');
        div.className = 'log-entry';
        div.innerHTML = `<div class="log-header"><span>${agent.icon} ${agent.name}</span></div><div class="log-content">${marked.parse(text)}</div>`;
        document.getElementById('logContainer').append(div);
        document.getElementById('logContainer').scrollTop = 99999;
    }
